$("a[href^='#']").click(function(e){e.preventDefault();var position=$($(this).attr("href")).offset().top;$("body, html").animate({scrollTop:position},1500);});var windowWidth=$(window).width();if(windowWidth>800){$(function(){$('a[href*=#]:not([href=#])').click(function(){$('.nav-dropdown').hide();if(location.pathname.replace(/^\//,'')==this.pathname.replace(/^\//,'')&&location.hostname==this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){$('html,body').animate({scrollTop:target.offset().top},1500);return false;}}});});}if(windowWidth<800){$(function(){$('a[href*=#]:not([href=#])').click(function(){$('nav ul').slideToggle();$('#nav-toggle').removeClass('active');$('.nav-list').hide();if(location.pathname.replace(/^\//,'')==this.pathname.replace(/^\//,'')&&location.hostname==this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){$('html,body').animate({scrollTop:target.offset().top},1500);return false;}}});});}